mapper.select:
SELECT
       COUNT(*) AS COUNT
  FROM REVIEW_LIKE
 WHERE R_NO = #{rNo}
   AND USER_NO = #{loginUno}

mapper.delete:
DELETE 
  FROM REVIEW_LIKE
 WHERE R_NO = #{rNo}
   AND USER_NO = #{loginUno}

mapper.insert:
INSERT 
  INTO REVIEW_LIKE
  (
  		R_NO
  	  , USER_NO
  )
VALUES
  (
  		#{rNo}
  	  , #{loginUno}
  )
